import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const response = await axios.get('/api/items');
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching items:', error);
    } finally {
      setLoading(false);
    }
  };



  const getTransactionTypeColor = (type) => {
    switch (type) {
      case 'lend': return '#3498db';
      case 'give_away': return '#2ecc71';
      case 'exchange': return '#f39c12';
      default: return '#95a5a6';
    }
  };

  if (loading) return <div style={{ textAlign: 'center', padding: '2rem' }}>Loading items...</div>;

  return (
    <div>
      <h1 style={{ textAlign: 'center', marginBottom: '2rem' }}>Available Items</h1>
      {items.length === 0 ? (
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          <p>No items available yet.</p>
          <Link to="/create" style={{ color: '#3498db' }}>Be the first to post an item!</Link>
        </div>
      ) : (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(320px, 1fr))', gap: '1.5rem' }}>
          {items.map(item => (
            <Link key={item.id} to={`/item/${item.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <div style={{
                background: 'rgba(255, 255, 255, 0.95)',
                backdropFilter: 'blur(10px)',
                borderRadius: '16px',
                boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                overflow: 'hidden',
                transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                cursor: 'pointer',
                height: '420px',
                display: 'flex',
                flexDirection: 'column',
                animation: 'fadeInUp 0.6s ease-out',
                animationFillMode: 'both'
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'translateY(-8px) scale(1.02)';
                e.currentTarget.style.boxShadow = '0 20px 40px rgba(0, 0, 0, 0.15)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'translateY(0) scale(1)';
                e.currentTarget.style.boxShadow = '0 8px 32px rgba(0, 0, 0, 0.1)';
              }}
              >
                {item.image_url && (
                  <img
                    src={item.image_url}
                    alt={item.name}
                    style={{ width: '100%', height: '220px', objectFit: 'cover', flexShrink: 0 }}
                  />
                )}
                <div style={{ padding: '1rem', flex: 1, display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
                  <div>
                    <h3 style={{ marginBottom: '0.5rem', fontSize: '1.2rem', lineHeight: '1.3' }}>{item.name}</h3>
                    <p style={{ 
                      color: '#666', 
                      marginBottom: '0.5rem', 
                      fontSize: '0.9rem', 
                      lineHeight: '1.4', 
                      height: '2.8em', 
                      overflow: 'hidden',
                      display: '-webkit-box',
                      WebkitLineClamp: 2,
                      WebkitBoxOrient: 'vertical',
                      textOverflow: 'ellipsis'
                    }}>
                      {item.description || 'No description available'}
                    </p>
                    {item.transaction_type === 'lend' && item.price_per_hour && (
                      <p style={{ color: '#2ecc71', fontWeight: 'bold', marginBottom: '0.5rem', fontSize: '0.9rem' }}>
                        ${item.price_per_hour}/hour
                      </p>
                    )}
                  </div>
                  <div>
                    <div style={{ marginBottom: '0.5rem' }}>
                      <span style={{
                        backgroundColor: '#95a5a6',
                        color: 'white',
                        padding: '0.2rem 0.5rem',
                        borderRadius: '12px',
                        fontSize: '0.7rem',
                        textTransform: 'uppercase',
                        marginRight: '0.5rem'
                      }}>
                        {item.category}
                      </span>
                      <span style={{
                        backgroundColor: getTransactionTypeColor(item.transaction_type),
                        color: 'white',
                        padding: '0.2rem 0.5rem',
                        borderRadius: '12px',
                        fontSize: '0.7rem',
                        textTransform: 'capitalize'
                      }}>
                        {item.transaction_type.replace('_', ' ')}
                      </span>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', fontSize: '0.8rem', color: '#666', marginBottom: '0.25rem' }}>
                      <span>
                        {item.transaction_type === 'lend' ? (
                          item.available_quantity === 0 ? (
                            <span style={{ color: '#e74c3c', fontWeight: 'bold' }}>All {item.category}s got lent</span>
                          ) : (
                            `Available: ${item.available_quantity}/${item.quantity}`
                          )
                        ) : (
                          `Qty: ${item.quantity}`
                        )}
                      </span>
                      <span>by {item.username}</span>
                    </div>
                    <div style={{ fontSize: '0.8rem', color: '#666', wordWrap: 'break-word' }}>
                      üìç {item.address}
                    </div>
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default ItemList;
