import React, { useState } from 'react';
import axios from 'axios';

const AppointmentModal = ({ item, onClose, onSuccess }) => {
  const [appointmentData, setAppointmentData] = useState({
    appointment_time: '',
    location: '',
    notes: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/appointments', {
        item_id: item.id,
        owner_id: item.user_id,
        appointment_time: appointmentData.appointment_time,
        location: appointmentData.location,
        notes: appointmentData.notes
      }, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      onSuccess();
      onClose();
    } catch (error) {
      setError(error.response?.data?.message || 'Failed to book appointment');
    } finally {
      setLoading(false);
    }
  };

  const getCurrentDateTime = () => {
    const now = new Date();
    now.setMinutes(now.getMinutes() - now.getTimezoneOffset());
    return now.toISOString().slice(0, 16);
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.7)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000
    }}>
      <div style={{
        background: 'rgba(255, 255, 255, 0.95)',
        backdropFilter: 'blur(20px)',
        borderRadius: '24px',
        boxShadow: '0 20px 60px rgba(0,0,0,0.3)',
        border: '1px solid rgba(255, 255, 255, 0.2)',
        padding: '2rem',
        maxWidth: '500px',
        width: '90%',
        maxHeight: '80vh',
        overflow: 'auto'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1.5rem' }}>
          <h2 style={{ 
            fontSize: '1.8rem',
            fontWeight: '700',
            background: 'linear-gradient(45deg, #ff0000, #ff7f00, #ffff00, #00ff00, #0000ff, #4b0082, #9400d3)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundSize: '400% 400%',
            animation: 'rainbow 3s ease-in-out infinite'
          }}>Book Appointment</h2>
          <button
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              fontSize: '1.5rem',
              cursor: 'pointer',
              color: '#666'
            }}
          >
            âœ•
          </button>
        </div>

        <div style={{ marginBottom: '1rem', padding: '1rem', background: '#f8f9fa', borderRadius: '8px' }}>
          <h3 style={{ marginBottom: '0.5rem' }}>{item.name}</h3>
          <p style={{ color: '#666', fontSize: '0.9rem' }}>Owner: {item.username}</p>
        </div>

        {error && (
          <div style={{ color: '#e74c3c', marginBottom: '1rem', textAlign: 'center' }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
              Appointment Date & Time *
            </label>
            <input
              type="datetime-local"
              value={appointmentData.appointment_time}
              onChange={(e) => setAppointmentData({...appointmentData, appointment_time: e.target.value})}
              min={getCurrentDateTime()}
              style={{
                width: '100%',
                padding: '0.75rem',
                border: '2px solid rgba(102, 126, 234, 0.2)',
                borderRadius: '12px',
                fontSize: '1rem',
                transition: 'all 0.3s ease',
                background: 'rgba(255, 255, 255, 0.8)',
                outline: 'none'
              }}
              required
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
              Meeting Location *
            </label>
            <input
              type="text"
              placeholder="e.g., Coffee shop on Main Street, My address, etc."
              value={appointmentData.location}
              onChange={(e) => setAppointmentData({...appointmentData, location: e.target.value})}
              style={{
                width: '100%',
                padding: '0.75rem',
                border: '2px solid rgba(102, 126, 234, 0.2)',
                borderRadius: '12px',
                fontSize: '1rem',
                transition: 'all 0.3s ease',
                background: 'rgba(255, 255, 255, 0.8)',
                outline: 'none'
              }}
              required
            />
          </div>

          <div style={{ marginBottom: '1.5rem' }}>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
              Additional Notes (optional)
            </label>
            <textarea
              placeholder="Any specific requirements or notes for the meeting..."
              value={appointmentData.notes}
              onChange={(e) => setAppointmentData({...appointmentData, notes: e.target.value})}
              rows="3"
              style={{
                width: '100%',
                padding: '0.75rem',
                border: '2px solid rgba(102, 126, 234, 0.2)',
                borderRadius: '12px',
                fontSize: '1rem',
                transition: 'all 0.3s ease',
                background: 'rgba(255, 255, 255, 0.8)',
                outline: 'none',
                resize: 'vertical'
              }}
            />
          </div>

          <div style={{ display: 'flex', gap: '1rem' }}>
            <button
              type="button"
              onClick={onClose}
              style={{
                flex: 1,
                padding: '0.75rem',
                background: '#95a5a6',
                color: 'white',
                border: 'none',
                borderRadius: '12px',
                cursor: 'pointer',
                fontSize: '1rem',
                fontWeight: '600'
              }}
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading}
              style={{
                flex: 1,
                padding: '0.75rem',
                background: loading ? '#95a5a6' : 'linear-gradient(135deg, #ff6b6b 0%, #feca57 100%)',
                color: 'white',
                border: 'none',
                borderRadius: '12px',
                cursor: loading ? 'not-allowed' : 'pointer',
                fontSize: '1rem',
                fontWeight: '600',
                boxShadow: loading ? 'none' : '0 4px 15px rgba(255, 107, 107, 0.6)'
              }}
            >
              {loading ? 'Booking...' : 'Book Appointment'}
            </button>
          </div>
        </form>

        <div style={{ marginTop: '1rem', padding: '1rem', background: '#e8f4fd', borderRadius: '8px', fontSize: '0.9rem' }}>
          <strong>ðŸ“‹ Note:</strong> The item owner will receive your appointment request and can confirm or decline it. 
          You'll get a reminder 30 minutes before the confirmed appointment.
        </div>
      </div>
    </div>
  );
};

export default AppointmentModal;
