import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Navbar = ({ user, logout }) => {
  const [requestCount, setRequestCount] = useState(0);

  useEffect(() => {
    if (user) {
      fetchRequestCount();
    }
  }, [user]);

  const fetchRequestCount = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/requests/count', {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setRequestCount(response.data.pending_received);
    } catch (error) {
      console.error('Error fetching request count:', error);
    }
  };
  return (
    <nav style={{
      backgroundColor: '#2c3e50',
      padding: '1rem 2rem',
      color: 'white',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    }}>
      <Link to="/" style={{ color: 'white', textDecoration: 'none', fontSize: '1.5rem', fontWeight: 'bold' }}>
        Item Exchange
      </Link>
      <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
        {user ? (
          <>
            <span>Welcome, {user.username}!</span>
            <Link to="/requests" style={{ color: 'white', textDecoration: 'none', position: 'relative', display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              Requests
              {requestCount > 0 && (
                <>
                  <span style={{
                    backgroundColor: '#e74c3c',
                    color: 'white',
                    borderRadius: '50%',
                    width: '20px',
                    height: '20px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '0.8rem',
                    fontWeight: 'bold'
                  }}>
                    {requestCount}
                  </span>
                  <span style={{ color: '#e74c3c', fontSize: '1.2rem', fontWeight: 'bold' }}>!</span>
                </>
              )}
            </Link>
            <Link to="/create" style={{ color: 'white', textDecoration: 'none', padding: '0.5rem 1rem', backgroundColor: '#3498db', borderRadius: '4px' }}>
              Post Item
            </Link>
            <button onClick={logout} style={{ padding: '0.5rem 1rem', backgroundColor: '#e74c3c', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" style={{ color: 'white', textDecoration: 'none' }}>Login</Link>
            <Link to="/register" style={{ color: 'white', textDecoration: 'none' }}>Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
